import React from "react";
import Books from "../../../models/booksEntity";
import { Link } from "react-router-dom";

export const SearchBook: React.FC<{ book: Books }> = (props) => {
  return (
    <div className="card mt-3 shadow p-3 mb-3 bg-body rounded">
      <div className="row g-0">
        <div className="col-md-2">
          <div className="d-none d-lg-block">
            {props.book.bookImage ? (
              <img
                src={props.book.bookImage}
                width="123"
                height="233"
                alt="Book"
              />
            ) : (
              <img
                src={require("../../../Images/BooksImages/book-luv2code-1000.png")}
                width="123"
                height="233"
                alt="Book"
              />
            )}
          </div>
          <div
            className="d-lg-none d-flex justify-content-center 
                        align-items-center"
          >
            {props.book.bookImage ? (
              <img
                src={props.book.bookImage}
                width="123"
                height="196"
                alt="Book"
              />
            ) : (
              <img
                src={require("../../../Images/BooksImages/book-luv2code-1000.png")}
                width="123"
                height="196"
                alt="Book"
              />
            )}
          </div>
        </div>
        <div className="col-md-6">
          <div className="card-body">
            <h5 className="card-title">{props.book.bookTitle}</h5>
            <h4>{props.book.bookAuthor}</h4>
            <p className="card-text">{props.book.bookDescription}</p>
          </div>
        </div>
        <div className="col-md-4 d-flex justify-content-center align-items-center">
          <Link
            className="btn btn-md main-color text-white"
            to={`/checkout/${props.book.bookId}`}
          >
            View Details
          </Link>
        </div>
      </div>
    </div>
  );
};
