import axios from "axios";
import { sessionHistory } from "../commons/history";
import { clearCurrentUser } from "../redux_reducers_actions/actions_auth/user.actions";
import appStore from "../redux_reducers_actions/allReducers";
import { useSelector } from "react-redux";

export const authHeader = () => {
  const currentSignInUser = useSelector((state: any) => state.user);

  return {
    "Content-Type": "application/json",
    Authorization: "Bearer " + currentSignInUser?.jwtAccessToken,
  };
};

export function handleResponseWithLoginCheck() {
  axios.interceptors.response.use(
    (response) => response,
    (error) => {
      const currentUser = appStore.getState().user;
      const isSignedIn = currentUser?.jwtAccessToken;
      const status = error?.response?.status;

      if (isSignedIn && [401, 403].includes(status)) {
        appStore.dispatch(clearCurrentUser());
        sessionHistory.push("/login");
      }
      return Promise.reject(error);
    }
  );
}
