import { UserActions } from "../actions_auth/user.actions";

export interface userState {
  userName: string | null;
  password: string | null;
  fullName: string | null;
  emailAddress: string | null;
  contactNumber: string | null;
  jwtAccessToken: string | null;
  userId?: number | null;
}

export const userInitialState: userState = {
  userName: null,
  password: null,
  fullName: null,
  emailAddress: null,
  contactNumber: null,
  jwtAccessToken: null,
  userId: null,
};

export const userReducer = (state = userInitialState, action: UserActions) => {
  switch (action.type) {
    case "SET_CURRENT_USER":
      localStorage.setItem("currentLoginUser", JSON.stringify(action.payload));
      return {
        ...state,
        user: action.payload,
      };

    case "CLEAR_CURRENT_USER":
      localStorage.removeItem("currentLoginUser");
      return null;

    default:
      const userStr = localStorage.getItem("currentLoginUser");
      if (userStr) {
        return JSON.parse(userStr);
      }
  }
};

export default userReducer;
