import { type } from "os";
import { SET_CURRENT_USER, CLEAR_CURRENT_USER } from "../../commons/types";

type userInitialState = {
  userName: string;
  password: string;
  fullName: string;
  emailAddress: string;
  contactNumber: string;
};

const userReducer = (state = {}, action: any) => {
  switch (action.type) {
    case SET_CURRENT_USER:
      localStorage.setItem("currentLoginUser", JSON.stringify(action.payload));
      return {
        ...state,
        user: action.payload,
      };

    case CLEAR_CURRENT_USER:
      localStorage.removeItem("currentLoginUser");
      return null;

    default:
      const userStr = localStorage.getItem("currentLoginUser");
      if (userStr) {
        return JSON.parse(userStr);
      }
  }
};

export default userReducer;
