import { useState, useEffect } from "react";
import { Link, Redirect } from "react-router-dom";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faUserCircle } from "@fortawesome/free-solid-svg-icons";
import User from "../../models/userEntity";
import authenticationService from "../../services/authentication.service";
import { AUTHENTICATION_BASE_URL } from "../../commons/constants";

export const LoginPage = () => {
  // Login User And SignUp User
  const [userName, setUserName] = useState("");
  const [password, setPassword] = useState("");

  // Displays
  const [displayWarning, setDisplayWarning] = useState(false);
  const [displaySuccess, setDisplaySuccess] = useState(false);

  // LOading
  const [loading, setLoading] = useState(false);

  const currentSigInUser = authenticationService.getCurrentUser();

  useEffect(() => {
    if (currentSigInUser?.userId) {
      // navigate
      <Redirect to="/profile" />;
    }
  }, [currentSigInUser]);

  async function userSignIn() {
    const loginUrl: string = AUTHENTICATION_BASE_URL + "/signIn";
    if (currentSigInUser?.userId && userName !== "" && password !== "") {
      const userRegistered: User = new User(userName, password, "", "", "");
      const loginRequestOptions = {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(userRegistered),
      };

      const submitLoginResponse = await fetch(
        loginUrl,
        loginRequestOptions
      ).then((response) => {
        alert(response);
        console.log(response.formData());
      });
      console.log(JSON.stringify(submitLoginResponse));

      //const submitLoginResponseJson = submitLoginResponse.json();

      /*if (!submitLoginResponse.ok) {
        throw new Error("Something went wrong!");
      }*/

      /* if (submitLoginResponseJson.jwtAccessToken) {
        localStorage.setItem("currentUser", JSON.stringify(response.data));
      }*/

      setUserName("");
      setPassword("");
      setLoading(true);
      setDisplayWarning(false);
      setDisplaySuccess(true);
      alert("User Successfully Login.");
      <Redirect to="/profile" />;
    } else {
      setLoading(false);
      setDisplayWarning(true);
      setDisplaySuccess(false);
    }
  }

  return (
    <div className="container mt-5">
      <div className="card ms-auto me-auto p-3 shadow-lg custom-card">
        <h3 className="card-text text-center">SignIn Form</h3>
        <FontAwesomeIcon
          className="ms-auto me-auto user-icon"
          icon={faUserCircle}
        />
        {displaySuccess && (
          <div className="alert alert-danger">User Successfully Login.</div>
        )}
        {displayWarning && (
          <div className="alert alert-danger">All Fields must be Filled.</div>
        )}

        <form method="POST">
          <div className="form-group">
            <label htmlFor="userName">User Name</label>
            <input
              type="text"
              name="userName"
              className="form-control"
              value={userName}
              onChange={(e) => setUserName(e.target.value)}
              required
            />
          </div>
          <div className="form-group">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              name="password"
              className="form-control"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
          <button
            className="btn btn-info w-100 mt-3"
            disabled={loading}
            onClick={userSignIn}
          >
            Sign In
          </button>
        </form>
        <Link
          to="/register"
          className="btn btn-link"
          style={{ color: "darkgray" }}
        >
          Create New Account!
        </Link>
      </div>
    </div>
  );
};
