import Review from "../../models/reviewEntity";
import { Link } from "react-router-dom";
import { Reviews } from "../Utils/Review";

export const LatestReviews: React.FC<{
  latestBookReviews: Review[];
  bookId: number | undefined;
  mobile: boolean;
}> = (props) => {
  return (
    <div className={props.mobile ? "mt-3" : "row mt-5"}>
      <div className={props.mobile ? "" : "col-sm-2 col-md-2"}>
        <h2>Latest Reviews: </h2>
      </div>
      <div className="col-sm-10 col-md-10">
        {props.latestBookReviews.length > 0 ? (
          <>
            {props.latestBookReviews.slice(0, 3).map((eachReview) => (
              <Reviews review={eachReview} key={eachReview.reviewId}></Reviews>
            ))}

            <div className="m-3">
              <Link
                type="button"
                className="btn main-color btn-md text-white"
                to={`/reviewList/${props.bookId}`}
              >
                Reach all reviews.
              </Link>
            </div>
          </>
        ) : (
          <div className="m-3">
            <p className="lead">Currently there are no reviews for this book</p>
          </div>
        )}
      </div>
    </div>
  );
};
